mapper.select:
SELECT id, league_name, event_time, status, home_team, score, away_team, half_score, asian_plate, size, full_details, half_time_details, page_url, is_resolved
FROM live_score where is_resolved = #{is_resolved} ORDER BY id desc
LIMIT #{start}, #{rows}

SELECT f.id, f.realid, f.t, f.s, f.x, f.y, f.q, f.su, f.av, f.attribute, case when f.h = 1 then 'h' else 'a' end as ha FROM full_details f WHERE f.live_score_id = #{live_score_id} AND f.attribute = 0 AND f.x < (select IFNULL(min(ffd.x),100000) from full_details ffd where ffd.t> f.t and ffd.live_score_id = f.live_score_id and ffd.realid = f.realid)
(1=1
AND f.x = #{item.x}
AND f.y = #{item.y}
)
Union select fd.id, fd.realid, fd.t, fd.s, fd.x, fd.y, fd.q, fd.su, fd.av, fd.attribute, case when fd.h = 1 then 'h' else 'a' end as ha from full_details fd inner join (SELECT realid,su,max(a) FROM (SELECT realid,su,count(1) as a FROM full_details WHERE live_score_id = #{live_score_id} AND attribute = 0 GROUP BY realid,su ) AS T GROUP BY T.realid) as TT on fd.live_score_id = #{live_score_id} AND fd.attribute = 0 and fd.realid = TT.realid AND fd.su = TT.su where fd.x < (select IFNULL(min(ffd.x),100000) from full_details ffd where ffd.t> fd.t and ffd.live_score_id = fd.live_score_id and ffd.realid = fd.realid) ORDER BY t desc
LIMIT #{start}, #{rows}

select count(1) FROM (SELECT f.id, f.realid, f.t, f.s, f.x, f.y, f.q, f.su, f.av, f.attribute FROM full_details f WHERE f.live_score_id = #{live_score_id} AND f.attribute = 0 AND f.x < (select IFNULL(min(ffd.x),100000) from full_details ffd where ffd.t> f.t and ffd.live_score_id = f.live_score_id and ffd.realid = f.realid)
(1=1
AND f.x = #{item.x}
AND f.y = #{item.y}
)
Union select fd.id, fd.realid, fd.t, fd.s, fd.x, fd.y, fd.q, fd.su, fd.av, fd.attribute from full_details fd inner join (SELECT realid,su,max(a) FROM (SELECT realid,su,count(1) as a FROM full_details WHERE live_score_id = #{live_score_id} AND attribute = 0 GROUP BY realid,su ) AS T GROUP BY T.realid) as TT on fd.live_score_id = #{live_score_id} AND fd.attribute = 0 and fd.realid = TT.realid AND fd.su = TT.su Where fd.x < (select IFNULL(min(ffd.x),100000) from full_details ffd where ffd.t> fd.t and ffd.live_score_id = fd.live_score_id and ffd.realid = fd.realid) ) as TTT

SELECT f.id, f.realid, f.t, f.s, f.x, f.y, f.q, f.su, f.av, f.attribute, case when f.h = 1 then 'h' else 'a' end as ha FROM half_time_details f WHERE f.live_score_id = #{live_score_id} AND f.attribute = 0 AND f.x < (select IFNULL(min(ffd.x),100000) from half_time_details ffd where ffd.t> f.t and ffd.live_score_id = f.live_score_id and ffd.realid = f.realid)
(1=1
AND f.x = #{item.x}
AND f.y = #{item.y}
)
Union select fd.id, fd.realid, fd.t, fd.s, fd.x, fd.y, fd.q, fd.su, fd.av, fd.attribute, case when fd.h = 1 then 'h' else 'a' end as ha from half_time_details fd inner join (SELECT realid,su,max(a) FROM (SELECT realid,su,count(1) as a FROM half_time_details WHERE live_score_id = #{live_score_id} AND attribute = 0 GROUP BY realid,su ) AS T GROUP BY T.realid) as TT on fd.live_score_id = #{live_score_id} AND fd.attribute = 0 and fd.realid = TT.realid AND fd.su = TT.su where fd.x < (select IFNULL(min(ffd.x),100000) from half_time_details ffd where ffd.t> fd.t and ffd.live_score_id = fd.live_score_id and ffd.realid = fd.realid) ORDER BY t desc
LIMIT #{start}, #{rows}

select count(1) FROM (SELECT f.id, f.realid, f.t, f.s, f.x, f.y, f.q, f.su, f.av, f.attribute FROM half_time_details f WHERE f.live_score_id = #{live_score_id} AND f.attribute = 0 AND f.x < (select IFNULL(min(ffd.x),100000) from half_time_details ffd where ffd.t> f.t and ffd.live_score_id = f.live_score_id and ffd.realid = f.realid)
(1=1
AND f.x = #{item.x}
AND f.y = #{item.y}
)
Union select fd.id, fd.realid, fd.t, fd.s, fd.x, fd.y, fd.q, fd.su, fd.av, fd.attribute from half_time_details fd inner join (SELECT realid,su,max(a) FROM (SELECT realid,su,count(1) as a FROM half_time_details WHERE live_score_id = #{live_score_id} AND attribute = 0 GROUP BY realid,su ) AS T GROUP BY T.realid) as TT on fd.live_score_id = #{live_score_id} AND fd.attribute = 0 and fd.realid = TT.realid AND fd.su = TT.su where fd.x < (select IFNULL(min(ffd.x),100000) from half_time_details ffd where ffd.t> fd.t and ffd.live_score_id = fd.live_score_id and ffd.realid = fd.realid) ) as TTT
