mapper.select:
SELECT id, league_name, event_time, status, home_team, score, away_team, half_score, asian_plate, size, full_details, half_time_details, page_url, is_resolved
FROM live_score where is_resolved = #{is_resolved}
AND event_date = #{event_date}
AND event_date = (select event_date from live_score where is_resolved = #{is_resolved} order by id desc limit 0,1)
AND league_name LIKE CONCAT('%',#{league_name},'%')
AND home_team LIKE CONCAT('%',#{home_team},'%')
AND away_team LIKE CONCAT('%',#{away_team},'%')
ORDER BY id DESC
LIMIT #{start}, #{rows}

SELECT COUNT(1) FROM live_score where is_resolved = #{is_resolved}
AND event_date = #{event_date}
AND event_date = (select event_date from live_score where is_resolved = #{is_resolved} order by id desc limit 0,1)
AND league_name LIKE CONCAT('%',#{league_name},'%')
AND home_team LIKE CONCAT('%',#{home_team},'%')
AND away_team LIKE CONCAT('%',#{away_team},'%')
